import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import App from '../App.css'
import { useNavigate } from "react-router-dom"
import { useState } from 'react';
const RegisterMUI = () => { 

  const [state, setState] = useState({
    name: "",
    email: "",
    password: ""
  });

  const handleInputChange = (event) => {
    setState((prevProps) => ({
      ...prevProps,
      [event.target.name]: event.target.value
    }));
  };

  const emailRegex=/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  const handleSubmit = (event) => {
    if((state.name == "") || (state.email == "") || (state.password == ""))
    {
      alert("Please Fill All Required Fields");
      event.preventDefault();
    }    
    else 
    if (!state.email.length) {
      alert('Please Enter Valid Email');
      event.preventDefault();
    } 
    else if (!emailRegex.test(state.email)) {
      alert('Please Enter Valid Email');
      event.preventDefault();
    }
    else if ((state.password.length <= 7) || (state.password.length >= 13)){
      alert('Please Enter Valid Password Minimum Length is 8 and Maximum Length is 12');
      event.preventDefault();
    }
   else{
    event.preventDefault();
    console.log(state);
     console.log("Name: "+ state.name +"Email: "+ state.email + " Password:  "+ state.password + ``);
     alert("Successfully Form Submit. Your Name: "+ state.name +" Email: "+ state.email +``);

  };
  return (
    <>
     <form  onSubmit={handleSubmit} method="post"className='regStyle'>
    <Box
      sx={{       
        alignItems: 'center',
        '& > :not(style)': { m: 1 },
      }}>
      <TextField
        helperText="Please enter your name"
        id="name"
        label="Name"
        value={name}
        onChange={handleInputChange}
      />
      <br></br>
       <TextField
        helperText="Please enter your email"
        id="email"
        label="Email"
        value={email}
        onChange={handleInputChange}
      />
      <br></br>
       <TextField
        helperText="Please enter your password"
        id="pwd"
        label="Password"
        type="password"
        placeholder="Password"
        value={pwd}
        onChange={handleInputChange}
      />
      <br></br>  
   <Button variant="contained" onClick={handleSubmit}>Register</Button>
   <h3>OR</h3>
   <Button variant="contained" onClick={redirectHandler}>Login</Button>
  </Box>
  </form>
    </>
  );
}
}
export default RegisterMUI;
