import * as React from 'react';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import { useState, useEffect } from 'react';
import axios from "axios";
import { Header } from '../module';

const CrudTable =()=>{

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}
const [id,setId]=useState("");
const [username,setName]=useState("");
const [email,setEmail]=useState("");
const [data,setData]=React.useState([])
const [open, setOpen] = useState(false);
const [opendata,setOpenData] = useState(false);
const handleOpen = () => {
    setOpen(true);

};
const handleOpenData = () =>{
    setOpenData(true);
}
const handleClose = () => {
    setOpen(false);
    setId('');
   setName('');
   setEmail('');
};
useEffect(() => {
    getData();
}, [])

function getData() {
    axios
    .get("http://localhost:3001/users")
    .then((response) => {
      setData(response.data);
    })
}
function addData() {
    axios.post('http://localhost:3001/users', {
         id: id,
         username: username,
         email: email
       })
         .then((response) => {
           getData(response.data);
         })
         setOpenData(false);
 }
 function editData(userid) {
    setOpen(true);
    const item = data[userid - 1];
    setId(item.id);
    setName(item.username)
    setEmail(item.email)

}
 function update() {
    axios.put('http://localhost:3001/users/${')
       .then((data) => {
          console.log("resp is", data)
        //   getData(response.data);
        })
        .catch((err)=>{
            console.log("RESP",err)
        })
     setOpen(false);   
}

function deleteUser(id) {
    axios.delete(`http://localhost:3001/users/${id}`)
    .then((response) => {
      getData(response.data);
    })
}
const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

  return (
      <>
      <Header/>
    {/* ADD Button */}
    <Button variant="outlined" onClick={handleOpenData}>Add User</Button>
    <Dialog open={opendata} onClose={handleClose}>
    <DialogTitle>Enter Detail's</DialogTitle>
    <DialogContent>
    <TextField autoFocus margin="dense" id="id" label="Id" type="Id" fullWidth variant="standard" value={id}
     onChange={(e) => setId(e.target.value)}/>
    <TextField autoFocus margin="dense" id="userName" label="UserName" type="userName" fullWidth variant="standard" value={username}
     onChange={(e) => setName(e.target.value)}/>
    <TextField autoFocus margin="dense" id="name" label="Email Address" type="email" fullWidth variant="standard" value={email}
     onChange={(e) => setEmail(e.target.value)}/>
    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Cancel</Button>
     <Button  onClick={addData}>Add Data</Button>
    </DialogActions>
    </Dialog>
{/* Edit */}

    <Dialog open={open} onClose={handleClose}>
    <DialogTitle>Edit User</DialogTitle>
    <DialogContent>
    <TextField autoFocus margin="dense" id="id" label="Id" type="Id" fullWidth variant="standard" value={id}
     onChange={(e) => setId(e.target.value)}/>
    <TextField autoFocus margin="dense" id="userName" label="UserName" type="userName" fullWidth variant="standard" value={username}
     onChange={(e) => setName(e.target.value)}/>
    <TextField autoFocus margin="dense" id="name" label="Email Address" type="email" fullWidth variant="standard" value={email}
     onChange={(e) => setEmail(e.target.value)}/>
    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Cancel</Button>
    <Button type="submit" onClick={update}>Update</Button>
    </DialogActions>
    </Dialog>
{/* Table  */}
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 700 }} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell align="right">ID</StyledTableCell>
            <StyledTableCell align="right">Name</StyledTableCell>
            <StyledTableCell align="right">Email</StyledTableCell>
            <StyledTableCell align="right">Action</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {data.map((row) => (
            <StyledTableRow key={row.name}>
              <StyledTableCell align="right">{row.id}</StyledTableCell>
              <StyledTableCell align="right">{row.username}</StyledTableCell>
              <StyledTableCell align="right">{row.email}</StyledTableCell>
              <StyledTableCell align="right">
                <Button  variant="contained" style={{marginRight:10}} onClick={() => editData(row.id)} >Edit</Button>
                <Button  variant="contained" onClick={() => deleteUser(row.id)}>Delete</Button> </StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </>
  );
}
export default CrudTable;
