                  }else{
                    forword=false;
                  }
            }else if(!forword){
                if(initialcount>0){
                    initialcount= initialcount-1;      
             }     
             }
            setCount(initialcount);
        },1000)
    }
    useEffect(()=>{
        Updater();
    },[])
    return(
        <>
            <h1>Counts: {count} </h1>
        </>
    )
