import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

const Edit =() =>{
    const [open, setOpen] = useState(false);
    const [opendata, setOpenData] = useState(false);
    const [data, setData] = useState([]);
    const [id, setId] = useState("");
    const [username, setUserName] = useState("");
    const [email, setEmail] = useState("");
     const classes = useStyles();
    const handleOpen = () => {
        setOpenData(true);
        setId();
        setUserName();
        setEmail();

    };
    const handleClose = () => {
        setOpen(false);
    };
    useEffect(() => {
        getData();
    }, [])

    function getData() {
        axios
        .get("http://localhost:3001/users")
        .then((response) => {
          setData(response.data);
        })
    }
    function editData(userid) {
        let item = data[userid - 1];
        setId(item.id);
        setUserName(item.username)
        setEmail(item.email)
        setOpen(true);
    }
    return (
        <>
        <div>
        <Button variant="outlined" onClick={handleOpen}>Edit</Button>
        <Dialog open={opendata} onClose={handleClose}>
        <DialogTitle>Edit Detail's</DialogTitle>
        <DialogContent>
        <TextField autoFocus margin="dense" id="id" label="Id" type="Id" fullWidth variant="standard"
        onChange={(e) => setId(e.target.value)}/>
        <TextField autoFocus margin="dense" id="userName" label="UserName" type="userName" fullWidth variant="standard"
         onChange={(e) => setUserName(e.target.value)}/>
        <TextField autoFocus margin="dense" id="name" label="Email Address" type="email" fullWidth variant="standard"
         onChange={(e) => setEmail(e.target.value)}/>
        </DialogContent>
        <DialogActions>
        {/* <Button onClick={handleClose}>Cancel</Button> */}
        <Button onClick={editData}>Edit</Button>
        </DialogActions>
        </Dialog>
        </div>
        </>
       )
    }
export default Edit;
